import React from 'react';
import { NavLink } from 'react-router-dom';
import '../styles.css';

// 1. Removed the import for the image as it's no longer needed
// import headerBackground from '../images/unnamed.png'; // This line is GONE

const Header = () => {
  const headerStyle = {
    // 2. Removed all background image related properties from here.
    // You can add other styles here if you want a different kind of background
    // (e.g., backgroundColor: '#f0f0f0', or keep it empty)
  };

  return ( // Make sure this 'return' is inside the component function
    <header className="header" style={headerStyle}>
      <h1>Restaurace v Pivovaru</h1>
      <nav className="navbar">
        <NavLink
          to="/"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Denní Menu
        </NavLink>
        <NavLink
          to="/drink-menu"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Nápojový Lístek
        </NavLink>
        <NavLink
          to="/salon"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Pronájem Salonku
        </NavLink>
        <NavLink
          to="/historie"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Historie
        </NavLink>
      </nav>
    </header>
  );
};

export default Header;
