import React from 'react';
import { NavLink } from 'react-router-dom';
import '../styles.css'; // Necháme import pro zbytek stylů, ale teď budou mít přednost inline styly

const Header = () => {
  const headerStyle = {
    backgroundImage: `url('images/hero-image.jpg')`,
    height: '75vh',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-end', // Umístíme navigační lištu dolů
    alignItems: 'center',
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    position: 'relative',
    textAlign: 'center',
    color: '#ffffff', // Světlý text pro header
    boxShadow: '0 8px 25px rgba(0, 0, 0, 0.25)',
    overflow: 'hidden',
    // Pseudoelement ::after nelze stylovat inline.
    // Zde ho nahradíme silnějším překrytím, pokud obrázek není dost tmavý.
    // background: 'linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.4)), url("images/hero-image.jpg") center/cover',
  };

  const navbarStyle = {
    backgroundColor: '#1f1d1b', // Tmavé pozadí pro navigaci (var(--color-background-dark))
    padding: '15px 0',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.3)',
    display: 'flex',
    justifyContent: 'center',
    borderBottom: 'none',
    width: '100%',
    position: 'relative',
    zIndex: '1001',
  };

  const linkStyle = { // Základní styl pro navigační odkazy
    fontFamily: 'Montserrat, sans-serif',
    fontWeight: '500',
    textTransform: 'uppercase',
    textDecoration: 'none',
    color: '#ffffff', // Světlý text odkazů (var(--color-light-text))
    padding: '10px 20px',
    margin: '0 10px',
    position: 'relative',
    letterSpacing: '1.2px',
    borderRadius: '4px',
  };

  const activeLinkStyle = { // Styl pro aktivní odkaz (aplikuje se navíc k linkStyle)
    color: '#b79b5c', // Zlatý text pro aktivní odkaz (var(--color-accent-gold))
    fontWeight: '600',
  };

  // Funkce pro získání stylu odkazu
  const getNavLinkStyle = ({ isActive }) => {
    // Vytvoříme kombinovaný styl: nejprve základní, pak přebijeme aktivním stylem
    return { ...linkStyle, ...(isActive ? activeLinkStyle : {}) };
  };

  return (
    <header className="header" style={headerStyle}>
      <nav className="navbar" style={navbarStyle}>
        <NavLink to="/" style={getNavLinkStyle}>
          Denní Menu
        </NavLink>
        <NavLink to="/drink-menu" style={getNavLinkStyle}>
          Nápojový Lístek
        </NavLink>
        <NavLink to="/salon" style={getNavLinkStyle}>
          Pronájem Salonku
        </NavLink>
        <NavLink to="/historie" style={getNavLinkStyle}>
          Historie
        </NavLink>
      </nav>
    </header>
  );
};

export default Header;
