import React from 'react';
import { NavLink } from 'react-router-dom';
import '../styles.css';
import headerBackground from '../images/unnamed.png'; // Import the image

const Header = () => {
  const headerStyle = {
    // Use template literals to correctly format the url() string
    backgroundImage: `url(${headerBackground})`,
    backgroundSize: 'cover', // Optional: Add properties to make the background look good
    backgroundPosition: 'center', // Optional
    backgroundRepeat: 'no-repeat', // Optional
  };

  return (
    <header className="header" style={headerStyle}>
      <h1>Restaurace v Pivovaru</h1>
      <nav className="navbar">
        <NavLink
          to="/"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Denní Menu
        </NavLink>
        <NavLink
          to="/drink-menu"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Nápojový Lístek
        </NavLink>
        <NavLink
          to="/salon"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Pronájem Salonku
        </NavLink>
        <NavLink
          to="/historie"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Historie
        </NavLink>
      </nav>
    </header>
  );
};

export default Header;
