import React from 'react';
import { NavLink } from 'react-router-dom';
import '../styles.css';

const Header = () => {
  const headerStyle = {
    // Toto je obrázek, který již obsahuje text "Vaříme pro vás..."
    backgroundImage: `url('images/hero-image.jpg')`,
  };

  return (
    <header className="header" style={headerStyle}>
      {/* Nadpis H1 už zde není, protože je součástí obrázku pozadí */}
      {/* Pokud byste chtěl zachovat text 'Restaurace v Pivovaru' i s obrázkem,
          musel by se obrázek upravit tak, aby text neobsahoval, a H1 by se vrátil.
          Nebo by se H1 musel stylovat tak, aby byl neviditelný / posunutý.
          Pro teď ho odstraníme, aby nedocházelo ke zdvojení. */}

      <nav className="navbar">
        <NavLink
          to="/"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Denní Menu
        </NavLink>
        <NavLink
          to="/drink-menu"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Nápojový Lístek
        </NavLink>
        <NavLink
          to="/salon"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Pronájem Salonku
        </NavLink>
        <NavLink
          to="/historie"
          className={({ isActive }) => (isActive ? 'active-link' : 'inactive-link')}
        >
          Historie
        </NavLink>
      </nav>
    </header>
  );
};

export default Header;
