import React from 'react';
import { NavLink } from 'react-router-dom';
import '../styles.css';

const Header = () => {
  const headerStyle = {
    backgroundImage: `url('images/hero-image.jpg')`, // Ujisti se, že máš tento obrázek v public/images
  };

  return (
    <header className="header" style={headerStyle}>
      <h1>Restaurace v Pivovaru</h1>
      <nav className="navbar">
        <NavLink to="/">Denní Menu</NavLink>
        <NavLink to="/drink-menu">Nápojový Lístek</NavLink>
        <NavLink to="/salon">Pronájem Salonku</NavLink>
      </nav>
    </header>
  );
};

export default Header;
